import React, { useState } from "react";

const App = ()=>{
  const [fullName, setFullName] = useState({
    fname : '',
    lname : '',
  });

  const inputEvent = (e)=>{
    const value = e.target.value;
    const name = e.target.name;
    setFullName((preValue)=>{
      if(name === 'fName'){
        console.log(value);
        console.log(preValue.lname);
        return {
          fname : value,
          lname : preValue.lname,
        };
      }
      else if(name === 'lName'){
        console.log(preValue.fname);
        return {
          fname : preValue.fname,
          lname : value,
        };
      }
    })
  }
  const onSubmits = (e)=>{
    e.preventDefault();
    alert("submitted");
  }
  return (
    <>
      <form onSubmit={onSubmits}>
        <div>
          <h1>Hello <span>{fullName.fname} {fullName.lname}</span></h1>
          <input type="text" placeholder="Enter Your Name" name = 'fName' onChange={inputEvent} value = {fullName.fname}></input>
          <input type="text" placeholder="Enter Your Last Name" name = 'lName' onChange={inputEvent} value={fullName.lname}></input>
          <button type="submit">Click Here</button>
        </div>
      </form>
    </>
  )
}

export default App;
