import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'

let currTime = new Date();
let time = currTime.getHours();
var greeting = "";

const h1Style = {
    padding: '50px',
    borderRadius: '45px',
    backgroundColor: 'rgba(252, 209, 209, 0.712)',
    color: 'rgb(184, 55, 55)',
    fontSize: '3rem'
}

if(time >= 3 && time <= 11)
  greeting = "Good Morning";
else if(time >= 12 && time <= 18)
  greeting = "Good Afternoon";
else
  greeting = "Good Night";

ReactDOM.render(
  <h1 style={h1Style}>Hello Sir, <span> {greeting} </span></h1>
  ,
  document.getElementById('root')
)
