import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'

let currTime = new Date();
let time = currTime.getHours();
var greeting = "";

const h1Style = {
  // padding: '50px',
  // borderRadius: '45px',
  // backgroundColor: 'rgba(252, 209, 209, 0.712)',
  // color: 'rgb(184, 55, 55)',
  // fontSize: '3rem'
}
const h1Span = { }

if(time >= 3 && time <= 11){
  greeting = `Good Morning`;
  h1Span.color = 'rgb(50, 94, 3)';
}
else if(time >= 12 && time <= 18){
  greeting = "Good Afternoon";
  h1Span.color = 'rgb(238, 156, 3)';
}
else{
  greeting = "Good Night";
  h1Span.color = 'Black';
}

console.log(time);

ReactDOM.render(
  // <h1 style={h1Style}>Hello Sir, <span style={h1Span}> {greeting} </span></h1>
  <h1>hello world</h1>
  ,
  document.getElementById('root')
)
