import React, { useState } from "react";
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

// const useStyles = makeStyles((theme) => ({
//   root: {
//     '& > *': {
//       margin: theme.spacing(1),
//     },
//   },
// }));


const App = ()=>{
  const [fullName, setFullName] = useState({
    fname : '',
    lname : '',
  });

  const inputEvent = (e)=>{
    // const value = e.target.value;
    // const name = e.target.name;
    const {name, value} = e.target;
    setFullName((preValue)=>{
      return {
        ...preValue,
        [name] : value,
      }
    })
  }
  const onSubmits = (e)=>{
    e.preventDefault();
    alert("submitted");
  }
  return (
    <>

      <form onSubmit={onSubmits}>
        <div>
        <Button variant="contained" color="primary">
        Primary
      </Button>
          <h1>Hello <span>{fullName.fname} {fullName.lname}</span></h1>
          <input type="text" placeholder="Enter Your Name" name = 'fname' onChange={inputEvent} value = {fullName.fname}></input>
          <input type="text" placeholder="Enter Your Last Name" name = 'lname' onChange={inputEvent} value={fullName.lname}></input>
          <button type="submit">Click Here</button>
        </div>
      </form>
    </>
  )
}

export default App;
