import React, { useState } from "react";


const App = ()=>{
  // const curTime = new Date().toLocaleTimeString();
  // const [time, setTime] = useState(curTime);

  // const setCurrTime = ()=>{
  //   let curTime = new Date().toLocaleTimeString();
  //   setTime(curTime);
  // }

  // setInterval(setCurrTime,1000);

  let count = 1;
  const [bg, setBg] = useState('lightblue');

  const changeBg = ()=>{
    console.log(count);
    if(count === 1 ){
      setBg('purple');
      count += 1;
      console.log(count);
    }
    else if(count === 2)
    {
      setBg('yellow');
      count += 1;
    }
    else if(count === 3)
    {
      setBg('orange');
      count += 1;
    }
    else{
      setBg('red');
      count = 1;

    }
  }
  return (
    <>
      {/* <h1> {time} </h1> */}
      <div style={{backgroundColor : bg}}>
        <button onClick={changeBg}>Change Background</button>
      </div>
    </>
  );
};

export default App;
