import React, { createContext, useState } from "react";

const App = ()=>{
  const [num, setNum] = useState(1);

  const change = ()=>{
    setNum(num+1);
    alertMsg();
  }
  const alertMsg = ()=>{
    alert("you clicked");
  }
  return (
    <>
      <button onClick={change}>Click Here {num} </button>
    </>
  )
}

export default App;
