const App = ()=>{
  let realName = "";
  const [name, setName] = useState();
  const [lastName, setLastName] = useState({
    fname : '',


  });

  const [fullName, setFullName] = useState();

  const inputEvent = (e)=>{
    setName(e.target.value);
  }
  const onSubmits = (e)=>{
    e.preventDefault();
    alert("submitted");
    // setFullName(name+" "+lastName);
  }
  return (
    <>
      <form onSubmit={onSubmits}>
        <div>
          <h1>Hello <span>{fullName}</span></h1>
          <input type="text" placeholder="Enter Your Name" name = 'fName' onChange={inputEvent} value = {name}></input>
          <input type="text" placeholder="Enter Your Last Name" name = 'lName' onChange={inputEvent} value={lastName}></input>
          <button type="submit">Click Here</button>
        </div>
      </form>
    </>
  )
}

export default App;
